<script type="application/javascript">
    function validate() {
        if ($('#host').val().indexOf('.')>0) $('#cont').removeClass('ui-disabled');
        else $('#cont').addClass('ui-disabled');
    }
    function go() {
        var url = $('#host').val().toLowerCase();
        var matches = url.match(/^https?\:\/\/([^\/:?#]+)(?:[\/:?#]|$)/i);
        var domain = matches && matches[1];
        if (domain) url=domain;
        if (url.substr(0,4)=='www.') url = url.substr(4);
        $('#host').val(url);
        $('#cont').addClass('ui-disabled').html('Wait...');
        $('form').submit();
    }

    $(document).ready(function(){
        validate();
    });
</script>
